import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { login } from '../api/authApi';
import { useAuth } from '../context/AuthContext';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate();
  const { setCurrentUser } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Simple validation
    if (!email || !password) {
      setError('Please enter both email and password');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const response = await login({ email, password });

      if (response.success) {
        console.log('Login successful:', response);
        setCurrentUser(response.user);
        navigate('/dashboard');
      } else {
        setError(response.error || 'Login failed. Please check your credentials.');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col md:flex-row bg-gray-100">
      {/* Left Column - Visual/Brand Panel */}
      <div className="md:w-1/2 flex flex-col items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600 p-8 relative overflow-hidden shadow-lg">
        {/* Abstract Shapes (More refined) */}
        <div className="absolute -top-24 -left-24 w-64 h-64 bg-white opacity-10 rounded-full mix-blend-screen"></div>
        <div className="absolute -bottom-24 -right-24 w-96 h-96 bg-blue-800 opacity-10 rounded-full mix-blend-screen"></div>
        <div className="absolute top-1/4 right-1/4 w-32 h-32 bg-purple-800 opacity-10 rounded-full mix-blend-screen"></div>

        {/* Branding Text - positioned more centrally and styled */}
        <div className="relative z-10 text-center text-white">
          <h1 className="text-6xl font-extrabold mb-4 drop-shadow-lg">Welcome Back!</h1>
          <p className="text-xl font-light max-w-sm mx-auto leading-relaxed drop-shadow-md">
            Pick up where you left off. Sign in to continue your journey with us.
          </p>
        </div>

        {/* Optional: Add a subtle image or illustration here */}
        {/* <img src="/path/to/your/image.png" alt="Illustration" className="absolute bottom-0 right-0 w-64 opacity-20"/> */}
      </div>

      {/* Right Column - Login Form */}
      <div className="md:w-1/2 flex flex-col justify-center py-12 px-6 sm:px-10 lg:px-16 bg-white font-sans shadow-xl rounded-lg md:rounded-none md:rounded-l-lg m-4 md:m-0 self-center w-full max-w-md md:max-w-none">
        <div className="max-w-md w-full mx-auto space-y-8">
          {/* App Name/Logo */}
          <div>
            <h2 className="text-3xl font-bold text-gray-900 text-center">
              Log In to Your Account
            </h2>
            <p className="mt-2 text-base text-gray-600 text-center">
              Welcome back! Please enter your details.
            </p>
          </div>

          {/* Error Display */}
          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
              <strong className="font-bold mr-2">Error:</strong>
              <span className="block sm:inline">{error}</span>
            </div>
          )}

          {/* Login Form */}
          <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
            <div className="rounded-md shadow-sm -space-y-px">
              <div>
                <label htmlFor="email-address" className="sr-only">
                  Email address
                </label>
                <input
                  id="email-address"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  className="appearance-none rounded-none relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm transition-all duration-200"
                  placeholder="Email address"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div>
                <label htmlFor="password" className="sr-only">
                  Password
                </label>
                <input
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="appearance-none rounded-none relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm transition-all duration-200"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  id="remember-me"
                  name="remember-me"
                  type="checkbox"
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                />
                <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
                  Remember me
                </label>
              </div>

              <div className="text-sm">
                <a href="#" className="font-medium text-blue-600 hover:text-blue-500">
                  Forgot your password?
                </a>
              </div>
            </div>

            <div>
