import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios for API calls
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

// --- Helper function to get token ---
// In a real app, this might be more complex (e.g., checking expiry)
const getToken = () => localStorage.getItem('token');

// --- Header Component ---
// (No changes needed for this component based on the request)
function Header() {
  return (
    <header className="bg-blue-600 text-white p-4 shadow-md rounded-t-lg">
      <div className="container mx-auto">
        <h1 className="text-2xl font-bold font-sans">GoalMaster Pro</h1>
        {/* Add navigation links here if needed */}
      </div>
    </header>
  );
}

// --- Goal Item Component ---
// Assuming the API returns goals with id, title, description, and status fields.
// Adjust field names (e.g., goal.name instead of goal.title) if your API structure differs.
function GoalItem({ goal }) {
  // Basic styling based on status
  const statusStyles = {
    'In Progress': 'bg-yellow-100 border-yellow-400 text-yellow-700',
    'Not Started': 'bg-gray-100 border-gray-400 text-gray-700',
    'Completed': 'bg-green-100 border-green-400 text-green-700', // Example for completed
  };

  // Default style if status is unknown or missing
  const defaultStyle = 'bg-white border-gray-300';
  const currentStyle = statusStyles[goal.status] || defaultStyle;

  return (
    <div className={`border rounded-lg p-4 mb-4 shadow-sm ${currentStyle}`}>
      {/* Display goal title - adjust 'goal.title' if your API uses a different field like 'goal.name' */}
      <h3 className="text-lg font-semibold font-sans mb-1">{goal.title || 'Untitled Goal'}</h3>
      {/* Display goal description */}
      <p className="text-sm text-gray-600 mb-2">{goal.description || 'No description.'}</p>
      {/* Display goal status */}
      <span className={`text-xs font-medium px-2.5 py-0.5 rounded border ${currentStyle}`}>
        {goal.status || 'Unknown Status'}
      </span>
      {/* Action Buttons - Add onClick handlers for functionality */}
      <div className="mt-2 space-x-2">
         <button className="text-xs bg-blue-500 hover:bg-blue-600 text-white font-bold py-1 px-2 rounded">
           Edit
         </button>
         <button className="text-xs bg-green-500 hover:bg-green-600 text-white font-bold py-1 px-2 rounded">
           Complete
         </button>
         <button className="text-xs bg-red-500 hover:bg-red-600 text-white font-bold py-1 px-2 rounded">
           Delete
         </button>
      </div>
    </div>
  );
}

// --- Main Content Area Component ---
// Now receives goals and loading/error state from the App component
function MainContent({ goals, isLoading, error }) {

  // Display loading state
  if (isLoading) {
    return <main className="container mx-auto p-6 flex-grow"><p>Loading goals...</p></main>;
  }

  // Display error state
  if (error) {
    return <main className="container mx-auto p-6 flex-grow"><p className="text-red-500">Error loading goals: {error}</p></main>;
  }

  return (
    <main className="container mx-auto p-6 flex-grow">
      <h2 className="text-xl font-semibold font-sans mb-4">Your Goals</h2>
      {/* Button to add a new goal - Add onClick handler */}
      <button className="mb-6 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded shadow">
        + Add New Goal
      </button>

      {/* List of goals */}
      <div>
        {goals.length > 0 ? (
          // Assuming API provides a unique 'id' field for each goal.
          // Change 'goal.id' to 'goal._id' if your API uses MongoDB's default.
          goals.map(goal => <GoalItem key={goal.id || goal._id} goal={goal} />)
        ) : (
          <p className="text-gray-500">No goals found. Create some goals!</p>
        )}
      </div>
    </main>
  );
}

// --- Footer Component ---
// (No changes needed for this component based on the request)
function Footer() {
  return (
    <footer className="bg-gray-100 text-gray-600 p-4 text-center text-sm rounded-b-lg mt-auto">
      Â© {new Date().getFullYear()} GoalMaster Pro. All rights reserved.
    </footer>
  );
}

// --- Main App Component ---
// Manages state for goals, loading, errors, and handles API fetching/auth.
export default function App() {
  const [goals, setGoals] = useState([]); // State for storing goals
  const [isLoading, setIsLoading] = useState(true); // State for loading status
  const [error, setError] = useState(null); // State for error messages
  const navigate = useNavigate(); // Hook for programmatic navigation

  // useEffect hook to fetch goals when the component mounts
  useEffect(() => {
    const fetchGoals = async () => {
      const token = getToken(); // Get token from localStorage

      // If no token, redirect to login page ('/')
      if (!token) {
        console.log('No token found, redirecting to login.');
        navigate('/');
        setIsLoading(false); // Stop loading as we are redirecting
        return;
      }

      // Reset state before fetching
      setIsLoading(true);
      setError(null);

      try {
        // Make API request to fetch goals
        const response = await axios.get('/goals', { // Make sure '/goals' is your correct API endpoint
          headers: {
            // Include Authorization header with Bearer token
            Authorization: `Bearer ${token}`,
          },
        });
        // Update goals state with data from API response
        setGoals(response.data);
      } catch (err) {
        console.error('Error fetching goals:', err);
        // Check if the error is an authorization error (e.g., 401 Unauthorized)
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          console.log('Unauthorized or Forbidden, clearing token and redirecting to login.');
          localStorage.removeItem('token'); // Remove invalid token
          setError('Authentication failed. Please log in again.'); // Set error message
          navigate('/'); // Redirect to login page
        } else {
          // Handle other types of errors (network error, server error, etc.)
          setError(err.message || 'Failed to fetch goals.');
        }
      } finally {
        // Set loading state to false once fetching is complete (success or error)
        setIsLoading(false);
      }
    };

    fetchGoals(); // Call the fetch function

    // Dependency array includes navigate to ensure it's stable,
    // though typically navigate itself doesn't change.
  }, [navigate]);

  return (
    // Use flex column and min-height to ensure footer stays at the bottom
    <div className="flex flex-col min-h-screen bg-gray-50 font-sans">
      <Header />
      {/* Pass goals, loading state, and error state to MainContent */}
      <MainContent goals={goals} isLoading={isLoading} error={error} />
      <Footer />
    </div>
  );
}
