import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { login } from '../api/authApi';
import { useAuth } from '../context/AuthContext';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate();
  const { setCurrentUser } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Simple validation
    if (!email || !password) {
      setError('Please enter both email and password');
      return;
    }

    try {
      setLoading(true);
      setError('');

      const response = await login({ email, password });

      if (response.success) {
        console.log('Login successful:', response);
        setCurrentUser(response.user);
        navigate('/dashboard');
      } else {
        setError(response.error || 'Login failed. Please check your credentials.');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('An unexpected error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen w-full flex justify-center items-center bg-gradient-to-b from-purple-400 via-purple-500 to-indigo-700 relative overflow-hidden">
      {/* Mountains and landscape elements */}
      <div className="absolute inset-0 z-0">
        {/* Stars */}
        <div className="absolute inset-0">
          {[...Array(50)].map((_, i) => (
            <div 
              key={i}
              className="absolute rounded-full bg-white"
              style={{
                width: Math.random() * 3 + 1 + 'px',
                height: Math.random() * 3 + 1 + 'px',
                top: Math.random() * 100 + '%',
                left: Math.random() * 100 + '%',
                opacity: Math.random() * 0.8 + 0.2
              }}
            />
          ))}
        </div>

        {/* Clouds */}
        <div className="absolute top-20 left-20 w-32 h-10 bg-purple-200 rounded-full opacity-40" />
        <div className="absolute top-30 left-60 w-40 h-12 bg-purple-200 rounded-full opacity-50" />
        <div className="absolute top-10 right-20 w-28 h-8 bg-purple-200 rounded-full opacity-40" />

        {/* Back Mountains */}
        <div className="absolute bottom-0 left-0 right-0 h-64 bg-indigo-900 opacity-90"
             style={{
               clipPath: 'polygon(0% 100%, 10% 70%, 20% 85%, 30% 60%, 40% 80%, 60% 40%, 75% 60%, 90% 40%, 100% 70%, 100% 100%)'
             }} />

        {/* Mid Mountains */}
        <div className="absolute bottom-0 left-0 right-0 h-52 bg-indigo-800 opacity-90"
             style={{
               clipPath: 'polygon(0% 100%, 5% 75%, 15% 90%, 25% 75%, 35% 90%, 45% 75%, 55% 85%, 65% 70%, 75% 85%, 85% 65%, 95% 80%, 100% 60%, 100% 100%)'
             }} />

        {/* Front Mountains and Trees */}
        <div className="absolute bottom-0 left-0 right-0 h-40 bg-indigo-900 opacity-95"
             style={{
               clipPath: 'polygon(0% 100%, 5% 85%, 10% 80%, 20% 90%, 30% 80%, 40% 95%, 50% 85%, 60% 95%, 70% 85%, 80% 90%, 90% 80%, 95% 85%, 100% 80%, 100% 100%)'
             }} />
      </div>

      {/* Login Glass Card */}
      <div className="w-full max-w-md p-6 bg-white/20 backdrop-blur-lg rounded-2xl shadow-xl z-10 mx-4">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-white mb-2">Login</h1>
        </div>

        {/* Error Display */}
        {error && (
          <div className="bg-red-400/30 backdrop-blur-sm border border-red-400 rounded-lg p-3 mb-6 text-white">
            <div className="flex items-center">
              <svg className="h-5 w-5 text-white mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
              <p className="text-sm">{error}</p>
            </div>
          </div>
        )}

        {/* Login Form */}
        <form onSubmit={handleSubmit} className="space-y-5">
          <div>
            <input
              type="text"
              placeholder="Username"
              className="w-full bg-white/30 backdrop-blur-sm border border-purple-200 rounded-lg px-4 py-3 text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-purple-300"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div>
            <input
              type="password"
              placeholder="Password"
              className="w-full bg-white/30 backdrop-blur-sm border border-purple-200 rounded-lg px-4 py-3 text-white placeholder-white/70 focus:outline-none focus:ring-2 focus:ring-purple-300"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="remember-me"
                name="remember-me"
                type="checkbox"
                className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300 rounded"
              />
              <label htmlFor="remember-me" className="ml-2 block text-sm text-white">
                Remember me
              </label>
            </div>

            <a href="#" className="text-sm text-white hover:text-purple-200 font-medium">
              Forgot Password?
            </a>
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full bg-white/25 backdrop-blur-sm hover:bg-white/40 text-white font-medium py-3 px-4 rounded-lg transition duration-300 ${
              loading ? 'opacity-70 cursor-not-allowed' : ''
            }`}
          >
            {loading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white inline-block" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Signing in...
              </>
            ) : (
              'Login'
            )}
          </button>
        </form>

        <div className="mt-6 text-center">
          <p className="text-white text-sm">
            Don't have an account?{' '}
            <Link to="/register" className="font-medium text-white hover:text-purple-200">
              Register
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
